sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // セクション名を取得
    var sectionnames = g_form.getSectionNames();
    var choiceValue = g_form.getValue('classification');
    var choiceLabel = g_form.getOption('classification', choiceValue).text;
    // 	alert(sectionnames);
    // 	alert(choiceLabel);

    if (isLoading || newValue === '') {
        onSectionDisplay(newValue);
        return;
    } else if (newValue === '1') {
        // 結婚祝金を選択した場合の処理
        // セクション（結婚祝金）を活性化する。
        onSectionDisplay(newValue);
        return;
    } else if (newValue === '2') {
        // 結婚祝金を選択した場合の処理
        // セクション（結婚祝金）を活性化する。
        onSectionDisplay(newValue);
        return;
    } else if (newValue === '3') {
        // 結婚祝金を選択した場合の処理
        // セクション（結婚祝金）を活性化する。
        onSectionDisplay(newValue);
        return;
    } else {
        // その他
        // すべて非表示にする
        onSectionDisplay(newValue);
        return;
    }
    //Type appropriate comment here, and begin script below
    function onSectionDisplay(newValue) {
		// Test check newValue
//         alert('newValue=' + newValue);
        if (newValue === '1') {
            // 1:結婚祝金を選択した場合の処理
            // セクション（結婚祝金）を活性化する
            g_form.setSectionDisplay(sectionnames[0], true);
            g_form.setSectionDisplay(sectionnames[1], false);
            g_form.setSectionDisplay(sectionnames[2], false);
        } else if (newValue === '2') {
            // 2:出産祝金を選択した場合の処理
            // セクション（出産祝金）を活性化する
            g_form.setSectionDisplay(sectionnames[0], false);
            g_form.setSectionDisplay(sectionnames[1], true);
            g_form.setSectionDisplay(sectionnames[2], false);
        } else if (newValue === '3') {
            // 3:死亡弔慰金を選択した場合の処理
            // セクション（死亡弔慰金）を活性化する
            g_form.setSectionDisplay(sectionnames[0], false);
            g_form.setSectionDisplay(sectionnames[1], false);
            g_form.setSectionDisplay(sectionnames[2], true);
        } else {
            // その他
            // すべて非表示にする
            g_form.setSectionDisplay(sectionnames[0], false);
            g_form.setSectionDisplay(sectionnames[1], false);
            g_form.setSectionDisplay(sectionnames[2], false);
        }
    }

}
