sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    // ロード時
//     // 割当て承認者を読み取り専用にする
//     g_form.setReadOnly('assigned_to', true);
// 	// ステータスを読み取り専用にする
//     g_form.setReadOnly('state', true);
    if (g_form.isNewRecord()) {
        // 新規申請時
        // 申請者にログインユーザをセット
        g_form.setValue('applicant', g_user.userID);
        // 実申請者にログインユーザをセット
        g_form.setValue('real_applicant', g_user.userID);
        // 申請区分を読み取り専用解除
        g_form.setReadOnly('classification', false);
    } else {
        // 新規以外の時
        // 申請区分を読み取り専用にする
        g_form.setReadOnly('classification', true);
    }
}
